
public class RelationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 8;
		if(a==b)			//Relational Operator
		{
			System.out.println("First number is equal to Second number");
		}
		else if(a!=b){
			System.out.println("First number is not equal to Second number");
		}
		
		System.out.println("");
		
		if(a>b)
		{
			System.out.println("First number is greater than second number");
		}
		else
		{
			System.out.println("first number is smaller than second number");
		}
	}

}
